import axios from "axios";

const axiosInstance = axios.create({baseURL: "http://localhost:3000"})

export async function fetchOrders(setOrders){
    try{
        const result = await axiosInstance.get('/orders', {withCredentials: true})
        setOrders(result.data.orders)
        return result.data
    }catch(error){
        console.log(error)
    }
}

export async function loggedStatus(){
    try{
        const loggStatus = await axiosInstance('/logged_in', {withCredentials: true})
        return console.log(loggStatus)
    }catch(error){
        console.log(error)
    }
}

