import React from 'react';
import CartProduct from './CartProduct';
import axios from 'axios';

export default function Cart({message, user, cart, removeFromCart, editOrderitem, productDescription, productName}) {

function checkoutprice(){
  if(cart.length>0){
  return cart.reduce((a, b) => parseInt(a) + parseInt(b.total), 0)
  }else{ return 'No article in your cart'}
}

function resetCart(){
 return  cart.map(items =>  removeFromCart(items.id) )
}
//  console.log(cart)

function checkout(data){
  axios.post('http://localhost:3000/orders', {
    order: {
      total: data,
      user_id: user.id
    }
  }, {withCredentials: true})
  .then(response => {
    console.log(response)

  })
  .then(resp => {
    resetCart()
  })

}


  return (
    <div className='container-fluid'>
        <div className=".flex-row">
        <div className='container'>
        {message ? message : null }
          <ol>
            {cart.length > 0 ? cart.map((data, index)=> {return <li key={index} ><CartProduct productName={productName} removeFromCart={removeFromCart} productDescription={productDescription} editOrderitem={editOrderitem} data={data}  /></li>}) : <h2>Your cart is empty</h2>}
          </ol>
          <div className="footercart">
            <div className='innerfootercart'>
              {cart.length > 0 ? <span>Total : ${checkoutprice()},00</span>: null }
              {cart.length > 0 ? <button onClick={() => checkout(checkoutprice())} className='btn btn-primary btnlogg'>Check-out</button> : null }
              {/* checkout(checkoutprice()) */}
            </div>
          </div>
            </div>
        </div>
    </div>
  )
}
