import React from 'react';
import { useForm } from 'react-hook-form';
import Product from './Product'

export default function IndexProducts({ products, createProduct, setTriggered }) {

  const { register, handleSubmit, reset } = useForm()

  return (


    // createProduct(data.name, data.description, data.price, data.image)
    //       setTriggered(true)
    //       reset()
    <div className='container cont'>
      <div className="container-fluid">
        <form onSubmit={handleSubmit((data) => {
          // createProduct(data.name, data.description, data.price, data.image[0])
          // setTriggered(true)
          // reset()
          // console.log(data.image[0])
          // console.log(data.image)
          const dat = new FormData();

          dat.append("product[image]", data.image.files);
          createProduct(dat)

        })}>
          <label htmlFor='name'>Product name :</label>
          <input {...register('name')} />
          <label htmlFor='name'>Description :</label>
          <input {...register('description')} />
          <label htmlFor='name'>Price :</label>
          <input {...register('price')} type='number' step={1} />
          <label htmlFor='image'>Image</label>
          <input {...register('image')} type='file' />
          <input type='submit' />

        </form>
        <div className="row">
        {products.length > 0 ? products.map((data, index) => {return <Product data={data} key={index}/>}) : <h3>No products</h3>}
        </div>
      </div>
    </div>
  )
}
