import axios from 'axios';
import React from 'react';

export default function ProductShops({cart, data, setCart}) {


function addProduct(e){
  e.preventDefault()
  console.log(data)
  // axios.get(`http://localhost:3000/shops/${data.id}`, { withCredentials: true })
  // .then(response => console.log(response))
  setCart([...cart, data])
}



  return (

        <div className="card cartcard">
          <img className="card-img-top imgcart" src={data.image_url} alt="Card cap"/>
            <div className="card-body">
            <h5 className="card-title">{data.name}</h5>
          <button onClick={(e) => addProduct(e)} className="btn btn-primary">Add to your cart</button>
          </div>
        </div>

  )
}
