import React from 'react';
import CartProduct from './CartProduct';
import axios from 'axios';

export default function Cart({user, cart, removeFromCart, editOrderitem, productDescription, productName}) {

function checkoutprice(){
  if(cart.length>0){
  return cart.reduce((a, b) => parseInt(a) + parseInt(b.total), 0)
  }else{ return 'No article in your cart'}
}

function resetCart(){
  const arr = cart.map((items) =>  { items })
 return  arr
}
//  console.log(cart)

function checkout(data){
  // axios.post('http://localhost:3000/orders', {
  //   order: {
