import React from 'react';
import CartProduct from './CartProduct';

export default function Cart({cart, shops, setTriggered}) {

function checkoutprice(){
  if(cart.length>0){
  return cart.reduce((a, b) => parseInt(a) + parseInt(b.total), 0)
  }else{ return 'No article in your cart'}
}

  return (
    <div className='container-fluid'>
        <div className=".flex-row">
        <div className='container'>
          <ol>
            {cart.length > 0 ? cart.map((data, index)=> {return <li key={index} ><CartProduct setTriggered={setTriggered} shops={shops} data={data}  /></li>}) : <h2>Your cart is empty</h2>}
          </ol>
          <div className="footercart">
            <div className='innerfootercart'>
               <span>Total : ${checkoutprice()},00</span>
              <button className='btn btn-primary btnlogg'>Check-out</button>
            </div>
          </div>
            </div>
        </div>
    </div>
  )
}
