import React from 'react';
import CartProduct from './CartProduct';
import axios from 'axios';
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchCart, checkout } from '../requests/ApiRequest';


export default function Cart({ setCart, setTriggered, setMessage, triggered, message, user, cart, removeFromCart, productDescription, productName}) {

const navigate = useNavigate()

useEffect(() => {
    fetchCart(cart, triggered, setTriggered, setCart, setMessage)
  }, [cart, triggered])


function checkoutprice(){
  if(cart.length>0){
  return cart.reduce((a, b) => parseInt(a) + parseInt(b.total), 0)
  }else{ return 'No article in your cart'}
}

function resetCart(){
 return  cart.map(items =>  removeFromCart(items.id) )
}


const checkingOut = async (price, user, resetCart) =>{
  console.log(user)
  console.log(user.id)
    try{
      const checkCartOut = await checkout(price, user.id, resetCart)
      return console.log(checkCartOut)
    }catch(error){
      console.log(error)
    }

  // axios.post('http://localhost:3000/orders', {
  //   order: {
  //     total: data,
  //     user_id: user.id
  //   }
  // }, {withCredentials: true})
  // .then(response => {
  //   console.log(response)

  // })
  // .then(resp => {
  //   resetCart()
  // })

}

 console.log(user)
  return (
    <div className='container-fluid'>
        <div className=".flex-row">
        <div className='container'>
            {message ? <h3 style={{textAlign: 'center', marginTop: '2%'}} >{message}</h3> : cart.length > 0  ? cart.map((data, index)=> {return <CartProduct  key={index} productName={productName} removeFromCart={removeFromCart} productDescription={productDescription} data={data}  />}) : 
            <div className='d-flex flex-column align-items-center justify-content-flex-end container-fluid col-12' >

            <h3  style={{textAlign: 'center', marginTop: '2%'}} >Your cart is empty ...</h3>
            <button style={{marginTop: '2%'}} className='btn btn-primary'  onClick={(e) => navigate('/account')}>Click here to see your(s) order(s)</button>
            </div>
             }  

          <div className="footercart">
            <div className='innerfootercart'>
              {cart.length > 0 ? <span>Total : ${checkoutprice()},00</span>: null }
              {cart.length > 0 ? <button onClick={() => checkingOut(checkoutprice())} className='btn btn-primary btnlogg'>Check-out</button> : null }
              {/* checkout(checkoutprice()) */}
            </div>
          </div>
            </div>
        </div>
    </div>
  )
}
