import React from 'react';
import { useForm } from 'react-hook-form';
import Product from './Product'

export default function IndexProducts({ products, createProduct }) {

  const { register, handleSubmit } = useForm()

  return (
    <div className='container'>
      <div className="container-fluid">
        <form onSubmit={handleSubmit((data) => {
          createProduct(data.name, data.description, data.price)
        })}>
          <label htmlFor='name'>Product name :</label>
          <input {...register('name')} />
          <label htmlFor='name'>Description :</label>
          <input {...register('description')} />
          <label htmlFor='name'>Price :</label>
          <input {...register('price')} type='number' step={1} />
          <input type='submit' />
        </form>
        {products ? products.map((data, index) => {<div className="row-1"> <Product data={data} key={index}/></div>}) : <h3>No products</h3>}
      </div>
    </div>
  )
}
