import axios from 'axios';
import React from 'react';
import { useForm } from 'react-hook-form';


export default function ProductShops({cart, data, setCart}) {

  const { register, handleSubmit, reset } = useForm()

function addProduct(e){
  e.preventDefault()
  console.log(data)
  // axios.get(`http://localhost:3000/shops/${data.id}`, { withCredentials: true })
  // .then(response => console.log(response))
  setCart([...cart, data])
}


async function addNewLineProduct(form){

    let res = await axios.post('http://localhost:3000/order_items', form)

     let datapost = res.form;
     console.log(datapost)


  }



  return (
    <form onSubmit={handleSubmit((prod) => {
      const form = new FormData();

      form.append('order_item[product_id]', data.id)
      form.append('order_item[quantity]', prod.quantity)
      form.append('order_item[unit_price]', data.price)


      addNewLineProduct(form)
      console.log(form)
      console.log(prod.quantity)
      console.log(data)
      })}>
        <div className="card ">
          <img className="card-img-top " src={data.image_url} alt="Card cap"/>
            <div className="card-body">

              <label htmlFor='quantity'>quantity</label>
            <input {...register('quantity')} type="number" id="quantity" name="quantity" step="1" />


            <h5 className="card-title">{data.name}</h5>
          <button type='submit'  className="btn btn-primary">Add to your cart</button>
          </div>
        </div>
      </form>
  )
}
