import Home from './Home';
import { BrowserRouter,  Routes, Route } from 'react-router-dom';
import Login from './Auth/Login';
import Registration from './Auth/Registration';
import React from 'react';
import { useState } from 'react';
import axios from 'axios';


function App() {

function loggingUser(email, password){
  axios.post("http://localhost:3000/login", {
    user: {
      email: email,
      password: password
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in ){
      setLoggedIn(true)
      setUser(response.data.user)
     console.log(response)
    }
  })
  .catch(err => {
    console.log('login error', err)
  })
}

function registrationUser(email, password, password_confirmation){
  axios.post("http://localhost:3000/users", {
    user: {
      email: email,
      password: password,
      password_confirmation: password_confirmation
    }
  }, { withCredentials: true })
  .then(response => {
    console.log('succed', response)
  })
  .catch(err => {
    console.log('login error', err)
  })
}


const [user, setUser] = useState({})
const [loggedIn, setLoggedIn] = useState(false)


  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={ <Home /> } />
          <Route path="/login" element={ <Login setLoggedIn={setLoggedIn} loggedIn={loggedIn} loggingUser={loggingUser} /> } />
          <Route path="/registration" element={ <Registration /> } />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
