import React from 'react';
import { useForm } from 'react-hook-form';


export default function Login({loggedIn, loggingUser, message, logout, omniauth}) {

const { register, handleSubmit } = useForm()



  return (
    <div  className='d-flex flex-md-column align-items-center justify-content-center container-fluid login col-md-3'>
      <h1 className='row'>Login</h1>
      <h3>{message ? message : null }</h3>
      <form className='container flex-md-row align-items-center justify-content-center' onSubmit={handleSubmit((data) =>{
       loggingUser(data.email, data.password)
      })}>
      <div className="row-md-6">
    <label htmlFor='validationCustom01' className="form-label">Email :</label>
    <input {...register('email', {required: true})} placeholder='email' type="email" className="form-control" id="validationCustom01" required/>
    <div className="valid-feedback">
      Looks good!
    </div>
  </div>
  <div className="row-md-6">
    <label htmlFor="validationCustom02" className="form-label">Password :</label>
    <input {...register('password')} placeholder='password' className="form-control" id="validationCustom02" required/>
    <div className="valid-feedback">
      Looks good!
    </div>
  </div>
  <input type='submit' className='btn btn-outline-secondary'/>
  </form>

    </div>
  )
}
