import Home from './Home';
import { BrowserRouter,  Routes, Route } from 'react-router-dom';
import Login from './Auth/Login';
import Registration from './Auth/Registration';
import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar'


function App() {

function loggingUser(email, password){
  axios.post("http://localhost:3000/login", {
    user: {
      email: email,
      password: password
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in){
      setLoggedIn(true)
      setUser(response.data.user)
      setMessage(response.data.message)
     console.log(response)
    }else{
      console.log(response)
      setMessage(response.data.message)
    }
  })
  .catch(err => {
    console.log('API error', err)
  })
}

function registrationUser(email, password, 
  password_confirmation, 
  first_name, last_name, 
  street_address_1, 
  street_address_2, 
  city, 
  state, 
  zipcode ){
  axios.post("http://localhost:3000/users", {
    user: {
      email: email,
      password: password,
      password_confirmation: password_confirmation,
      first_name: first_name,
      last_name: last_name,
      address_attributes: {
      street_address_1: street_address_1,
      street_address_2: street_address_2,
      city: city,
      state: state,
      zipcode: zipcode}
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in){
    setUser(response.data.user)
    setLoggedIn(true)
    setMessage('')
    console.log('succed', response)
    }else{
      setMessage(response.data.errors)
    }
  })
  .catch(err => {
    console.log('serv error', err)
  })
}

function logout(){
  axios.delete('http://localhost:3000/logout')
  .then(response => {
    if(response.data.logged_out && loggedIn){
      setLoggedIn(false)
      setUser({})
    }
  })
}

useEffect(() => {
  axios.get('http://localhost:3000/logged_in', {withCredentials: true})
  .then(response => {
    if(response.data.logged_in){
      console.log('use effect fonctionne')
      setLoggedIn(true)
      setUser(response.data.user)
    }else if(!loggedIn){
      setLoggedIn(false)
      setUser({})
    }
  })
}, [])

const [message, setMessage] = useState('')
const [user, setUser] = useState({})
const [loggedIn, setLoggedIn] = useState(false)


  return (
    <div className="App">
      <Navbar loggedIn={loggedIn} logout={logout}/>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={ <Home /> } />
          <Route path="/login" element={ <Login message={message} loggedIn={loggedIn} loggingUser={loggingUser} logout={logout} /> } />
          <Route path="/registration" element={ <Registration registrationUser={registrationUser} message={message} /> } />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
