import React from 'react';
import { useForm } from 'react-hook-form';

export default function CartProduct({data, shops}) {

const { register, handleSubmit} = useForm(data.quantity)


function productName(){
  if(shops.length>0){
  return (shops.find(element => element.id === data.product_id )).name
  }else{
    return 'Loading...'
  }
}

function productDescription(){
  if(shops.length>0){
  return (shops.find(element => element.id === data.product_id )).description
  }else{
    return 'Loading...'
  }
}


  return (
    <div className="containercart">
      <div className="leftcart">
        <span className="cartproduct"><strong>Product </strong> : {productName()}</span>
        <span className="cartproduct"><strong>Description</strong> : {productDescription()}</span>
      </div>
      <div className="rightcart ">
        <span className="cartproduct"><strong>Unit price</strong> : {data.unit_price}</span>
        <span className="cartproduct"><strong>Quantity</strong> : {data.quantity}</span>
        <input type='number' step={1} value={data.quantity} />
        <span className="cartproduct"><strong>Total</strong> : ${data.total}</span>
        <div className='btnedit'>
        <button className='btn btn-secondary btnediteach'>edit</button>
        <button className='btn btn-secondary btnediteach'>remove</button>
        </div>
      </div>

    </div>
  )
}
