   setProducts([...products, datapost.product])

}


function loggingUser(email, password){
  axios.post("http://localhost:3000/login", {
    user: {
      email: email,
      password: password
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in){
      setLoggedIn(true)
      setUser(response.data.user)
      setMessage(response.data.message)
     console.log(response)
    }else{
      console.log(response)
      setMessage(response.data.message)
    }
  })
  .catch(err => {
    setMessage('Incorrect email or password')
    console.log('API error', err)
  })
}

function registrationUser(email, password, 
  password_confirmation, 
  first_name, last_name, 
  street_address_1, 
  street_address_2, 
  city, 
  state, 
  zipcode ){
  axios.post("http://localhost:3000/users", {
    user: {
      email: email,
      password: password,
      password_confirmation: password_confirmation,
      first_name: first_name,
      last_name: last_name,
      address_attributes: {
      street_address_1: street_address_1,
      street_address_2: street_address_2,
      city: city,
      state: state,
      zipcode: zipcode}
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in){
    setUser(response.data.user)
    setLoggedIn(true)
    setMessage('')
    console.log('succed', response)
    }else{
      setMessage(response.data.errors)
    }
  })
  .catch(err => {
    setMessage('address must be complete')
    // console.log('serv error', err)
  })
}

function logout(){
  axios.delete('http://localhost:3000/logout', {withCredentials: true})
  .then(response => {
    if(response.data.logged_out && loggedIn === true){
      setLoggedIn(false)
      setUser({})
      setMessage('')
      console.log(response)
    }else if(!response.data.logged_out && loggedIn === false){
      console.log('problem')
    }
  })
}

