import axios from 'axios';
import React from 'react';

export default function ProductShops({data, setCart}) {


function addProduct(e){
  e.preventDefault()
  console.log(data)
  // axios.get(`http://localhost:3000/shops/${data.id}`, { withCredentials: true })
  // .then(response => console.log(response))
  setCart(data)
}



  return (
        <a onClick={(e) => addProduct(e)} className='alinkproduct' href='/product/id=' >
        <div className="card ">
          <img className="card-img-top" src={data.image_url} alt="Card cap"/>
            <div className="card-body">
            <h5 className="card-title">{data.name}</h5>
          <button className="btn btn-primary">Add to your cart</button>
          </div>
        </div>
         </a>
  )
}
