import Home from './Home';
import { BrowserRouter,  Routes, Route } from 'react-router-dom';
import Login from './Auth/Login';
import Registration from './Auth/Registration';
import React from 'react';
import { useState } from 'react';
import axios from 'axios';


function App() {

function loggingUser(email, password){
  axios.post("http://localhost:3000/login", {
    user: {
      email: email,
      password: password
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in ){
      setLoggedIn(true)
      setUser(response.data.user)
     console.log(response)
    }else{
      setMessage(response.data.message)
    }
  })
  .catch(err => {
    console.log('login error', err)
  })
}

function registrationUser(email, password, password_confirmation, street_address_1, street_address_2, city, zipcode ){
  axios.post("http://localhost:3000/users", {
    user: {
      email: email,
      password: password,
      password_confirmation: password_confirmation,
      street_address_1: street_address_1,
      street_address_2: street_address_2,
      city: city,
      zipcode: zipcode
    }
  }, { withCredentials: true })
  .then(response => {
    if(response.data.logged_in){
    setUser(response.data.user)
    setLoggedIn(true)
    console.log('succed', response)
    }else{
      console.log('error', response)

      setMessage(response.data.errors)
      console.log(response.data.errors)
    }
  })
  .catch(err => {
    console.log('login error', err)
  })
}

const [message, setMessage] = useState('')
const [user, setUser] = useState({})
const [loggedIn, setLoggedIn] = useState(false)


  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={ <Home /> } />
          <Route path="/login" element={ <Login message={message} loggedIn={loggedIn} loggingUser={loggingUser} /> } />
          <Route path="/registration" element={ <Registration registrationUser={registrationUser} message={message} /> } />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
