  console.log(data)
  // axios.get(`http://localhost:3000/shops/${data.id}`, { withCredentials: true })
  // .then(response => console.log(response))
  setCart([...cart, data])
}


function addnewlineproduct(){
  const form = new FormData();

  form.append('order_item')



}




  return (
    <form>
        <div className="card ">
          <img className="card-img-top " src={data.image_url} alt="Card cap"/>
