export default function Cart({shops, setCart, setTriggered, setMessage, triggered, message, user, cart}) {

const navigate = useNavigate()

useEffect(() => {
    fetchCart(cart, triggered, setTriggered, setCart, setMessage)
  }, [cart, triggered])


function checkoutprice(){
  if(cart.length>0){
