import React from 'react';

export default function CartProduct({data, shops}) {


function productName(){
  if(shops.length>0){
  return (shops.find(element => element.id === data.product_id )).name
  }else{
    return 'Loading...'
  }
}

function productDescription(){
  if(shops.length>0){
  return (shops.find(element => element.id === data.product_id )).description
  }else{
    return 'Loading...'
  }
}


  return (
    <div className="containercart">
      <div className="leftcart">
        <span className="cartproduct">{productName()}</span>
        <span className="cartproduct">{productDescription()}</span>
      </div>
      <div className="rightcart ">
        <span className="cartproduct">Unit price : {data.unit_price}</span>
        <span className="cartproduct">Quantity : {data.quantity}</span>
        <span className="cartproduct">Total : ${data.total}</span>
        <div className='btnedit'>
        <button className='btn btn-secondary btnediteach'>edit article</button>
        <button className='btn btn-secondary btnediteach'>remove article</button>
        </div>
      </div>

    </div>
  )
}
